import { createWebHistory, createRouter } from "vue-router";

const routes = [
    {
        path: "/",
        name: "to-do-list",
        component: () => import("./components/To-Do-List")
    },
    {
        path: "/addevent",
        name: "addevent",
        component: () => import("./components/Add-Event")
    },
    {
        path: "/deleteevent",
        name: "deleteevent",
        component: () => import("./components/Delete-Event")
    },
    {
        path: "/updateevent",
        name: "updateevent",
        component: () => import("./components/Update-Event")
    }

];

const router = createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
